import React, { Component } from "react";
import './App.css';
import Button from 'react-bootstrap/Button'
import Navbar from 'react-bootstrap/Navbar'
import Container from 'react-bootstrap/Container'
import Nav from 'react-bootstrap/Nav'
import 'bootstrap/dist/css/bootstrap.min.css'
import { render } from 'react-dom';


class Home extends Component{
    state ={users:[] }

    componentDidMount() {
        this.interval = setInterval(
            fetch('/api/1')
      .then(res => res.json())
      .then(users => this.setState({ users})), 1000
            );

            setInterval( () => {
                this.setState({
                  curTime : new Date().toLocaleString()
                })
              },1000)
    //   fetch('/api/1')
    //   .then(res => res.json())
    //   .then(users => this.setState({ users}));
    }



render(){
  return (

    <div className="App">

     <Navbar bg="primary" variant="dark">
    <Container>
    <Navbar.Brand href="#home">Sample App</Navbar.Brand>
    <Nav className="me-auto">
      <Nav.Link href="#home">MyLinkedIn</Nav.Link>
      <Nav.Link href="#features">My GitHub</Nav.Link>
      <Nav.Link href="#pricing">Project Repo</Nav.Link>
    </Nav>
    </Container>
  </Navbar>

  <Container>
    <h1>Current Time -{this.state.curTime} </h1>
    <br />
    {this.state.users.map(user => <li key={user.StopId}>
           {user.Route} {user.hours} : {user.Min}
           </li>)}

  </Container>
    </div>
  );
}
}
export default Home;
