import React, { Component } from "react";
import './App.css';
import Navbar from 'react-bootstrap/Navbar'
import Container from 'react-bootstrap/Container'
import Nav from 'react-bootstrap/Nav'
import { ListGroup, Table } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css'
import { Row,Col } from "react-bootstrap";
//import useLocation from "react-router-dom";


class Appointments extends Component{
    state ={stop1:[], stop2:[] }

    componentDidMount() {

        setInterval( () => {
            fetch('/api/getAll')
            .then(res => res.json())
            .then(stop1 => this.setState({ stop1}))
          },1000)


 const propdata = this.props.pid;       
        setInterval( () => {
            fetch(`/api/getAppointments/${propdata}`)
            .then(res => res.json())
            .then(stop2 => this.setState({ stop2}))
          },1000)


            // For live time update
            setInterval( () => {
                this.setState({
                  curTime : new Date().toLocaleString('en-GB')
                })
              },1000)

    }



render(){
  return (

    <div className="App">

     <Navbar bg="primary" variant="dark">
    <Container>
    <Navbar.Brand href="#home">Sample App</Navbar.Brand>
    <Nav className="me-auto">
      <Nav.Link href="https://www.linkedin.com/in/itsmeetkumarpatel/">MyLinkedIn</Nav.Link>
      <Nav.Link href="https://github.com/itsmeetkumar">My GitHub</Nav.Link>
      <Nav.Link href="https://github.com/itsmeetkumar/ReactProject">Project Repo</Nav.Link>
    </Nav>
    </Container>
  </Navbar>

  <Container>

    <Row>
      <Table>
         <thead>
             <th>Name</th>
             <th>Time</th>
             <th>Kind</th>
         </thead>
     </Table>

    <Col sm={8}>
    {this.state.stop2.map(user => <ListGroup.Item as="li" >  {user.name}  {user.kind}   
    </ListGroup.Item>)}
<hr />
    </Col>
    </Row>

  </Container>
    </div>
  );
}
}
export default Home;
